<?phpsession_start();include("../../inc/koneksi.php");$s = isset($_POST["search"]) ? mysqli_real_escape_string($conn, $_POST["search"]["value"]) : "";$p = isset($_POST["start"]) && $_POST["start"] != "" && is_numeric($_POST["start"]) ? intval($_POST["start"]) : 0;$e = isset($_POST["length"]) && $_POST["length"] != "" && is_numeric($_POST["length"]) ? intval($_POST["length"]) : 10;$c = array("username", "nama_user", "level");$o = isset($_POST["order"]) ? array($c[intval($_POST["order"][0]["column"])], strtoupper($_POST["order"][0]["dir"])) : array($c[0], "DESC");$result["draw"] = $_POST["draw"];$query = mysqli_query($conn, "SELECT COUNT(*) AS total FROM users WHERE kode_user<>".$_SESSION["kode"]);$row = mysqli_fetch_assoc($query);$result["recordsTotal"] = intval($row["total"]);$query = mysqli_query($conn, "SELECT COUNT(*) AS filtered FROM users WHERE kode_user<>".$_SESSION["kode"]." AND (username LIKE '%{$s}%' OR nama_user LIKE '%{$s}%')");$row = mysqli_fetch_assoc($query);$result["recordsFiltered"] = intval($row["filtered"]);$query = mysqli_query($conn, "SELECT * FROM users WHERE kode_user<>".$_SESSION["kode"]." AND (username LIKE '%{$s}%' OR nama_user LIKE '%{$s}%') ORDER BY {$o[0]} {$o[1]} LIMIT {$p},{$e}");$result["data"] = array();$baris = 0;$gudang = $_SESSION["level"] == "gudang";while($row = mysqli_fetch_assoc($query)) {	$level = intval($row["level"]);	$level_user = "-";	if($level == 1) {		$level_user = "Gudang";	} else if($level == 2) {		$level_user = "Pegawai";	} else if($level == 3) {		$level_user = "Biro Pengadaan";	}	else if($level == 4) {		$level_user = "Manager";	}	if($gudang) {		$tombol = array(			"<button class='btn btn-success btn-tooltip btn-aksi-sm-left' onclick='edit({$baris});' title='Edit'><i class='fa fa-pencil'></i></button>",			"<button class='btn btn-danger btn-tooltip btn-aksi-sm-right' onclick='hapus({$baris});' title='Hapus'><i class='fa fa-trash'></i></button>"		);		array_push($result["data"], array(			htmlspecialchars($row["username"]),			htmlspecialchars($row["nama_user"]),			$level_user,			"<center><div style='width:88px;'>{$tombol[0]}{$tombol[1]}</div></center>",			array(				$row["kode_user"],				$level			)		));		$baris++;	} else {		array_push($result["data"], array(			htmlspecialchars($row["username"]),			htmlspecialchars($row["nama_user"]),			$level_user		));	}}echo json_encode($result);?>